import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      username: "",
      pasticipant: []
    };
  }

  handleFormChange = event => {
    console.log(event.target.value);

    // let username = { ...this.state.username,  };
    this.setState({
      [event.target.name]: event.target.value
    });
  };
  submitFormHandle = event => {
    const newParticipate = [...newParticipate, this.state.username];
    this.setState({
      participant: newParticipate
    });
  };

  render() {
    return (
      <>
        <div className='login'>
          <div className='login-triangle' />
          <h2 className='login-header'>Github Workshop</h2>
          <form className='login-container'>
            <p>
              <input
                value={this.state.username}
                name='username'
                placeholder='username'
                onChange={this.handleFormChange}
              />
            </p>
            <p>
              <input
                type='submit'
                defaultValue='join workshop'
                onClick={this.submitFormHandle}
              />
            </p>
          </form>
        </div>
      </>
    );
  }
}

export default App;
